package fr.endide.application.data.generator;

import com.vaadin.exampledata.DataType;
import com.vaadin.exampledata.ExampleDataGenerator;
import com.vaadin.flow.spring.annotation.SpringComponent;
import fr.endide.application.data.Role;
import fr.endide.application.data.entity.Student;
import fr.endide.application.data.entity.User;
import fr.endide.application.data.service.StudentRepository;
import fr.endide.application.data.service.UserRepository;
import java.time.LocalDateTime;
import java.util.Base64;
import java.util.Collections;
import java.util.Set;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.boot.CommandLineRunner;
import org.springframework.context.ApplicationContext;
import org.springframework.context.annotation.Bean;
import org.springframework.security.crypto.password.PasswordEncoder;

@SpringComponent
public class DataGenerator {

    @Bean
    public CommandLineRunner loadData(PasswordEncoder passwordEncoder, ApplicationContext ctx,
            UserRepository userRepository, StudentRepository studentRepository) {
        return args -> {
            Logger logger = LoggerFactory.getLogger(getClass());
            if (userRepository.count() != 0L) {
                logger.info("Using existing database");
                return;
            }
            int seed = 123;

            logger.info("Generating demo data");

            logger.info("... generating 2 User entities...");
            User user = new User();
            user.setName("John Normal");
            user.setUsername("user");
            user.setHashedPassword(passwordEncoder.encode("user"));
            user.setProfilePicture(Base64.getDecoder().decode(
                    ""));
            user.setRoles(Collections.singleton(Role.USER));
            userRepository.save(user);
            User admin = new User();
            admin.setName("Emma Powerful");
            admin.setUsername("admin");
            admin.setHashedPassword(passwordEncoder.encode("admin"));
            admin.setProfilePicture(Base64.getDecoder().decode(
                    ""));
            admin.setRoles(Set.of(Role.USER, Role.ADMIN));
            userRepository.save(admin);
            logger.info("... generating 100 Student entities...");
            ExampleDataGenerator<Student> studentRepositoryGenerator = new ExampleDataGenerator<>(Student.class,
                    LocalDateTime.of(2022, 9, 12, 0, 0, 0));
            studentRepositoryGenerator.setData(Student::setUsername, DataType.WORD);
            studentRepositoryGenerator.setData(Student::setFirstName, DataType.FIRST_NAME);
            studentRepositoryGenerator.setData(Student::setLastName, DataType.LAST_NAME);
            studentRepositoryGenerator.setData(Student::setEmail, DataType.EMAIL);
            studentRepositoryGenerator.setData(Student::setPassword, DataType.TWO_WORDS);
            studentRepository.saveAll(studentRepositoryGenerator.create(100, seed));

            logger.info("Generated demo data");
        };
    }

}
